function Parent() {
  const [count, setCount] = useState(0);
  
  // 每次 Parent render，都会创建新的函数
  const handleClick = () => {
    console.log('clicked');
  };
  
  return (
    <div>
      <Memochild onclick={handleClick}/> {/* memo 失效了！ */}
      <button onclick={() => setCount(count + 1)}>计数：{count}</button>
