function Parent() {
  const [count, setCount] = useState(0);
  
  // 用 useCallback 包裹，只要依赖不变，函数引用就不变
  const handleClick = useCallback(() => {
    console.log('clicked');
  }, []); // 空数组表示永远不变
  
  return (
    <div>
      <Memochild onClick="{handleClick}"> {/* 现在 memo 生效了！ */}
      <button onClick={() => setCount(count + 1)}>计数：{count}</button>
    </div>
  );
}

